import React, { useState, useEffect } from "react";
import axios from "axios";
import "../css/Login.css";

const Lead = () => {
  const clientid = "909daba4eb0b4c8e8978c71d3cc3685f";
  const redirect_uri = "http://localhost:3000/lead";
  const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize";
  const RESPONSE_TYPE = "token";

  const [token, setToken] = useState("");
  const [artists, setArtists] = useState([]);
  const [genres, setGenres] = useState([]);

  useEffect(() => {
    const hash = window.location.hash;
    let token = window.localStorage.getItem("token");

    if (!token && hash) {
      token = hash
        .substring(1)
        .split("&")
        .find((elem) => elem.startsWith("access_token"))
        .split("=")[1];

      window.location.hash = "";
      window.localStorage.setItem("token", token);
    }

    setToken(token);
    console.log(token);
  }, []);

  const logout = () => {
    setToken("");
    window.localStorage.removeItem("token");
  };

  const searchTop = async (e) => {
    e.preventDefault();
    const { data } = await axios.get(
      "https://api.spotify.com/v1/me/top/artists",
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );

    const topArtists = data.items.slice(0, 3); // Get the top 3 artists
    const artistNames = topArtists.map((artist) => artist.name); // Extract artist names
    const artistGenres = topArtists.map((artist) => artist.genres[0]); // Extract genres

    setArtists(artistNames);
    setGenres(artistGenres);
  };

  const renderArtists = () => {
    return artists.map((artist, index) => (
      <div key={index}>
        <h2>{artist}</h2>
        <h2>{genres[index]}</h2>
      </div>
    ));
  };

  // const renderArtists = () => {
  //   return artists.map((artist) => (
  //     <div key={artist.id}>
  //       {artist.images.length ? (
  //         <img width={"100%"} src={artist.images[0].url} alt="" />
  //       ) : (
  //         <div>No Image</div>
  //       )}
  //       {artist.name}
  //     </div>
  //   ));
  // };

  return (
    <>
      <div>
        {!token ? (
          <>
            <div class="login-body">
              <div class="wrapper">
                <h1>Login:</h1>
                <button
                  className="button"
                  onClick={() =>
                    (window.location.href = `${AUTH_ENDPOINT}?client_id=${clientid}&redirect_uri=${redirect_uri}&response_type=${RESPONSE_TYPE}&scope=user-top-read`)
                  }
                >
                  Login to Spotify
                </button>
              </div>
            </div>
          </>
        ) : (
          <button className="button" onClick={logout}>
            Logout
          </button>
        )}
        <button onClick={searchTop}>Get Top</button>
        {renderArtist()}
      </div>
    </>
  );
};

export default Lead;
